// JavaScript Document
import React from 'react';
import StateParks from '../pages/stateParks';

//Material UI
import Typography from '@material-ui/core/Typography';
import Link from '@material-ui/core/Link';
import Grid from "@material-ui/core/Grid";

const id ='FL';
const endpoint = `stateCode=${id}`;

//Smart Component
class StateActivities extends React.Component {
	//declaring state and new object
	state ={
		activityListing:[],
	}

//calling fetchData function
	componentDidMount(){
		this.fetchActivities();
}

//fetching API
fetchActivities(){
fetch(`https://developer.nps.gov/api/v1/activities?${endpoint}&api_key=YpbDDtsNwQRi13JXZXiN7DnEIusWnKQLsCZW11xq`)
		.then(results =>{
		return results.json();
	}
			 ).then(data =>{
		let mList = data.data.map((use)=>{
			return(
			<Typography variant="body" align="left">
      			<Link  color="inherit" href="/">
					<div>{use.name}</div>
				</Link>{' '}
			 </Typography>	
			)
		})
		this.setState({StateActivities:mList});
	})
}
	 render() { 
  return (
<Grid container>
	<Grid item xs={6}>
	  	<StateParks />
	</Grid>
	<Grid item xs={6}>

	  	<Grid container style={styles.container}>
	  	 <Grid item xs={12}>
	  		<h1 style={styles.item}> State National Activities</h1>
	  	</Grid> 
	  	<Grid item xs={6} >
	  <h4 style={styles.item}> Filter parks by activity</h4>
	  		<div style={styles.activities}>
	 			{this.state.StateActivities}
	   		</div>
	  	</Grid> 
	 </Grid>
	</Grid> 
</Grid> 

  );
 }
}
export default StateActivities

const styles ={
	container:{
		border:'1px solid green',
		justifyContent:'center',

	},
	item:{
		textAlign:'center',
	},
	activities:{
		columns: '2 auto',
		border:'1px solid red',
		padding:'5%',
	},
}
