// JavaScript Document
import React from 'react';
import StateActivities from '../pages/stateActivities';

//Material UI
import Typography from '@material-ui/core/Typography';
import Link from '@material-ui/core/Link';
import Grid from "@material-ui/core/Grid";

//Smart Component
class StateParks extends React.Component {
	//declaring state and new object
	state ={
		parkListing: [],
	}

//calling fetchData function
	componentDidMount(){
	this.fetchParks();
}

//fetching API
 fetchParks(){
	fetch('https://developer.nps.gov/api/v1/parks?parkCode=bepa&api_key=YpbDDtsNwQRi13JXZXiN7DnEIusWnKQLsCZW11xq')
		.then(results =>{
		return results.json();
	}).then(data =>{

		let mList = data.data.map((use, i)=>{
			return(
				<Grid container key={i} style={styles.container}>
	  				<Grid item xs={12} md={12} style={styles.topRow}>
						<h1> State National Park</h1>
						<Grid item xs={12} md={11} style={styles.moreImages}>				
							{use.images.map(p => {
 							return (<li key={p} style={styles.indImgs}><img src={p.url} alt={p.altText} style={{width: 80, height: 80, borderRadius: 80/ 2, padding: '1%'}}/></li>
							)})}
						</Grid>
	   				</Grid> 

	 				<Grid item xs={12} md={4}style={styles.bottomRow}>
						<Grid item xs={12} style={styles.h2}>
							<StateActivities />
	  					</Grid> 
	   				</Grid> 
				</Grid>  	


			)
		})

		this.setState({StateParks:mList});
	})
} 

	 render() { 
  return (
  <div>
	  {this.state.StateParks}		
	  </div>
  );
 }
}
export default StateParks

const styles ={
	container:{
		marginTop:'1%',
		backgroundColor:'#313638',
		border:'1px solid red',
		padding:'1%',
		color:'white',
	},
	links:{
		height:'auto',
	},
	parks:{
		textAlign:'center',
		border:'1px solid blue',
	}
}
