// JavaScript Document
import React from 'react';
//import StateActivities from '../pages/stateActivities';

//Images & Icons
import image from '../images/placeholder.jpg'

//Material UI
import Link from '@material-ui/core/Link';
import Grid from "@material-ui/core/Grid";
import Box from '@material-ui/core/Box';

const id ='FL';
const endpoint = `stateCode=${id}&limit=2`;

//Smart Component
class StateParks extends React.Component {
	//declaring state and new object
	state ={
		parkListing: [],
	}

//calling fetchData function
	componentDidMount(){
	this.fetchParks();
}

//fetching API
fetchParks(){
	fetch(`https://developer.nps.gov/api/v1/parks?${endpoint}&api_key=YpbDDtsNwQRi13JXZXiN7DnEIusWnKQLsCZW11xq`)
		.then(results =>{
		return results.json();
	}).then(data =>{
		let mList = data.data.map((use, i)=>{
			return(

	 <Grid container>
	  	 <Grid item xs={12}>
	  	<h1> State National Parks</h1>
	  </Grid> 
	  <Grid item xs={12}>

	  <Grid container>
	  <Grid item xs={12} md={6}>
	  	<Box component="li" display="block" p={2} m={1}>
      	<Link color="inherit" href="https://www.huffpost.com/entry/why-do-people-blog-the-be_b_8178624">
		<img src={use.images[0].url} alt={use.images[0].altText} style={{width: '50%'}}/>
				</Link>
	  	{' '}
	  </Box>
	  </Grid> 
	 </Grid>

	   </Grid>
	   </Grid>	
			)
		})

		this.setState({StateParks:mList});
	})
}
	fetchParks(){ }

	 render() { 
  return (
  <div> 
	  {this.state.StateParks}		
	</div>
  );
 }
}
export default StateParks
