// JavaScript Document
import React from 'react';

//Material UI
import Typography from '@material-ui/core/Typography';
import Link from '@material-ui/core/Link';
import Grid from "@material-ui/core/Grid";


//Smart Component
class State_Activities extends React.Component {
	//declaring state and new object
	state ={
		activityListing:[]
	}

//calling fetchData function
	componentDidMount(){
		this.fetchActivities();
}

//fetching API
fetchActivities(){
 fetch('https://developer.nps.gov/api/v1/activities?statecode=FL&results=1&api_key=YpbDDtsNwQRi13JXZXiN7DnEIusWnKQLsCZW11xq')
		.then(results =>{
		return results.json();
	}).then(data =>{

		let aList = data.data.map((use)=>{
			return(
				<li key={this.props.id}>
				<span key={use.results}>
				<Link color="inherit" href="/">
{use.activities}


</Link>
				</span>
				</li>
			)
		})

		this.setState({State_Activities:aList});
	})
}
	 render() { 
  return (
  <Grid container style={styles.container}>
	   <Grid item xs={12} md={1} style={styles.links}>
	  <Link />
	   </Grid>
	  {this.state.State_Activities}		
	  </Grid>

  );
 }
}
export default State_Activities

const styles ={
	container:{
		marginTop:'1%',
		border:'1px solid red',
	},
	h1:{
		textAlign:'center',
		fontSize:'3em',
		color: theme.palette.secondary.main

	},
	pImg:{
		textAlign:'right',
		width:'100%',
	},
	proImg:{
		textAlign:'right',
		marginTop:'1%',
	},
	proInfo:{
		marginTop:'-1%',
		textAlign:'left',
		color:theme.palette.secondary.main,
		paddingBottom:'5%',
	},
}
