// JavaScript Document
import React from 'react';
import States from '../USA_states/states';
import { makeStyles } from '@material-ui/core/styles';

//images
//import logo2 from '../../images/ptBtn3.png';

//Material UI
import Typography from '@material-ui/core/Typography';
import Link from '@material-ui/core/Link';
import Grid from "@material-ui/core/Grid";
import Facebook from '@material-ui/icons/Facebook';
import Instagram from '@material-ui/icons/Instagram';
import Twitter from '@material-ui/icons/Twitter';
import Youtube from '@material-ui/icons/YouTube';

//styles
const useStyles = makeStyles(theme => ({
root:{				
		height:'auto',
		flexGrow: 1,
		overflow:'hidden',
		textAlign:'left',
	},
container:{
		marginTop:'-2%',
		justifyContent:'center',
	},
h2:{
		fontSize:'1.5em',
		textAlign:'left',
		color:theme.palette.secondary.contrastText,
		fontSize:'larger',
	},
a:{
		color:theme.palette.secondary.main,
		textDecoration:'none',
	},
about:{
		paddingRight:'4%',
		marginBottom:'5%',
		color:theme.palette.secondary.contrastText,
	},
states:{
	fontSize:'1.5em',
		textAlign:'left',
		color:theme.palette.secondary.contrastText,
		fontSize:'larger',
		marginTop:'-1%',
		marginBottom:'15%',
	},
connect:{
		display:'block',
		marginTop:'10%',
		paddingLeft:'35%',
	color:'white',
	},
	copyright:{
		color:theme.palette.secondary.main,
		marginTop:'-4%',
	},
}));

const Copyright = () => {
  const classes = useStyles();
  return (
    <Typography variant="body2" align="left" className={classes.copyright}>
      {'Copyright Â© '}
      <Link color="inherit" href="/">
        Parks2Go
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

//exporting a class footer
export default function Footer() {
  const classes = useStyles();

  return(
    <div className={classes.root}>
      <Grid container>

	  <Grid item xs={12} md={3} className={classes.about}>
		<h2 className={classes.h2}>About Us</h2>
					<p><a className={classes.a} href='http://localhost:3000/About'>Parks2Go</a> ipsum dolor sit amet. The hotel, the casino. The Corleone Family wants to buy you out. I donât like violence, Tom. Iâm a businessman; blood is a big expense.</p>
        </Grid>

        <Grid item xs={12} md={8}>
			<h2 className={classes.states}>Parks2Go</h2>
	  		<States />
	  	</Grid>

      <Grid item xs={12} md={1}>
	  <h2 className={classes.h2}>Connect</h2>
	  <Facebook className={classes.connect}/>
	  <Instagram className={classes.connect}/>
	  <Twitter className={classes.connect}/>
	  <Youtube className={classes.connect} />
      </Grid>

		<Grid item xs={12} md={12}><Copyright /></Grid>
      </Grid>
    </div>
  );
}
