// JavaScript Document
import React from 'react';
import ImageGallery from 'react-image-gallery';
import Grid from "@material-ui/core/Grid";

//Smart Component
class ImgGallery extends React.Component {
	//declaring state and new object
	state ={
		imgListing: [],
	}

//calling fetchData function
	componentDidMount(){
	this.fetchData();
}

//fetch Api data and map json results in a list format
fetchData(){
	const urlArray=[];
	const images2 = [];

 fetch('https://developer.nps.gov/api/v1/parks?parkCode=bepa&api_key=YpbDDtsNwQRi13JXZXiN7DnEIusWnKQLsCZW11xq')
		.then(results =>{
		return results.json();
	}
	).then(data =>{
let mList = data.data.map((use, i)=>{
	return(
		<div key={i}>				
		{use.images.map(p => {
		let url = p.url;
		let urlString = url.toString();

		var res = urlString.split("https://www.nps.gov/common/uploads/structured_data/");

			urlArray.push(res);

		var url2 = urlArray.toString();
		var res2 = url2.split(",");

	for( var i = 0; i < urlArray.length; i++)
	{ if ( res2[i] === "") { res2.splice(i, 1); }}

	for( var i = 0; i < res2.length; i++)
		{
	 images2.push({
    original: `https://www.nps.gov/common/uploads/structured_data/${res2[i]}?w=500&h=300`,
    thumbnail: `https://www.nps.gov/common/uploads/structured_data/${res2[i]}?w=150&h=50`,
  },)
	}	
 		return (	

		<ImageGallery key={i} items={images2}/>
			)})}

		   </div>
			)
		})
		this.setState({ImgGallery:mList});
	})
}
	 render() { 
  return (
	  <div >
	  <Grid container>
	  	<Grid item xs={12} md={4}>	
	  {this.state.ImgGallery}
	  </Grid>
	  </Grid>
		</div>			
  	);
  }
}

export default ImgGallery

