// JavaScript Document
import React from 'react';

//Material UI
import Link from '@material-ui/core/Link';
import Grid from "@material-ui/core/Grid";

 let sCode = window.location.pathname;
 let newSC = sCode.split('/StateParks/');
 newSC.shift();

const targetId = newSC;
const endpoint = `stateCode=${targetId}&limit=2`;


//Smart Component
class ParkGridStateParks extends React.Component {
	  constructor(props) {
        super(props);
        this.btnTapped = this
            .btnTapped
            .bind(this);
    }

	//declaring state and new object
	state ={
		parkListing: [],
	}

btnTapped() {
	var tId = event.target.nodeName;
	//var parkId = nodeName;
	console.log("id: "+parkId);
	//alert(tId);	
	 console.log('tapped');
} 

//calling fetchData function
	componentDidMount(){
	this.fetchParks();
}

//fetching API
 fetchParks(){
	fetch(`https://developer.nps.gov/api/v1/parks?${endpoint}&api_key=YpbDDtsNwQRi13JXZXiN7DnEIusWnKQLsCZW11xq`)
		.then(results =>{
		return results.json();
	}).then(data =>{

		let mList = data.data.map((use, i)=>{
			var parkId = use.parkCode
			console.log(parkId);
			return(
				<Link key={i}>
				<div onClick={this.btnTapped}>
				<img id={use.parkCode} src={use.images[1].url} alt={use.images[1].altText} style={{width:'100%'}}/>
				<h2 style={styles.h2}>{use.name}</h2>
				<h3 style={styles.h3}>{use.addresses[1].city}, {use.addresses[1].stateCode}</h3>
				</div>
				</Link>
			)
		})
		this.setState({ParkGridStateParks:mList});
	})
} 
	 render() { 
  return (
  	<div> 
	  <Grid container >
	  	 <Grid item xs={12}>
	  		<h1 style={styles.h1}>{targetId} State National Parks</h1>
	  	</Grid> 
	  	<Grid item xs={12}>
	  				<Link />
	  			</Grid> 
	 		</Grid>

	  {this.state.ParkGridStateParks}		
	</div>
  );
 }
}
export default ParkGridStateParks
const styles ={
	h1:{
		textAlign:'center',
	},
	h2:{
		marginTop:'-8%',
		color:'white',
	},
	h3:{
		marginTop:'-2%',
		color:'white',
	},
}
