	}

//calling fetchData function
	componentDidMount(){
	this.fetchData();
}

//fetch Api data and map json results in a list format
	fetchData(){
				let script = document.createElement('script');
		script.src = `http://www.recipepuppy.com/api/?callback=easyToDo`;
		document.body.append(script);

	//fetch('https://joshbloom.github.io/dws1/data/hikersguide.json')
		//.then(results =>{
		//return results.json();
	//}).then(data =>{
		let data = results.json
		let mList = data.posts.map((use)=>{
			return(
				<li key={this.props.id}>
				<span key={use.results}>
				<img width="25%" src={use.imageURL} alt={use.subtitle} />
				<h2>{use.title}</h2>
				<p>Ingredients: {use.text}</p>
				<Link href='{use.moreLink}'>{use.moreLink}</Link><br /><br /><br />
				</span>
				</li>
			)
		})
		this.setState({main:mList});
	//})
}
	 render() { 
  return (
		<div>{this.state.main}</div>			
  	);
  }
}

export default Main
