// JavaScript Document
import React from 'react';
import States from '../USA_states/states';
import { makeStyles } from '@material-ui/core/styles';

//images
//import logo2 from '../../images/ptBtn3.png';

//Material UI
import Typography from '@material-ui/core/Typography';
import Link from '@material-ui/core/Link';
import Grid from "@material-ui/core/Grid";
import GitHub from '@material-ui/icons/GitHub';
import LinkedIn from '@material-ui/icons/LinkedIn';
import Youtube from '@material-ui/icons/YouTube';

//styles
const useStyles = makeStyles(theme => ({
root:{				
		height:'auto',
		flexGrow: 1,
		overflow:'hidden',
		textAlign:'left',
	},
container:{
		marginTop:'-2%',
		justifyContent:'center',
	},
h2:{
		fontSize:'1.5em',
		textAlign:'left',
		color:theme.palette.secondary.contrastText,
		fontSize:'larger',
	},
a:{
		color:theme.palette.secondary.main,
		textDecoration:'none',
	},
about:{
		paddingRight:'4%',
		marginBottom:'5%',
		color:theme.palette.secondary.contrastText,
	},
states:{
	fontSize:'1.5em',
		textAlign:'left',
		color:theme.palette.secondary.contrastText,
		fontSize:'larger',
		marginTop:'-1%',
		marginBottom:'15%',
	[theme.breakpoints.up('md')]: {
    	marginTop: '3%',
		marginBottom:'11.5%',
		marginLeft:'1%',
	},
	},
connect:{
		fontSize:'1.5em',
		textAlign:'left',
		color:theme.palette.secondary.contrastText,
		fontSize:'larger',
		marginTop:'5%',
	[theme.breakpoints.up('md')]: {
    	marginTop: '30%',
		marginLeft:'-8%',
	},
	},
	socialMedia:{
		display:'inline-block',
		transform: 'scale(2)',
		marginTop:'10%',
		marginLeft:'20%',
		paddingRight:'1%',
		justifyContent:'center',
	color:theme.palette.secondary.contrastText,
		[theme.breakpoints.up('md')]: {
			display:'block',
			//float:'right',
    	marginTop: '40%',
		paddingBottom:'2%',
			marginLeft:'-8%',
	},
	},
	copyright:{
		color:theme.palette.secondary.main,
		marginTop:'15%',
	},
}));

const Copyright = () => {
  const classes = useStyles();
  return (
    <Typography variant="body2" align="left" className={classes.copyright}>
      {'Copyright Â© '}
      <Link color="inherit" href="/">
        Parks2Go
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

//exporting a class footer
export default function Footer() {
  const classes = useStyles();

  return(
    <div className={classes.root}>
      <Grid container>

	  <Grid item xs={12} md={3} className={classes.about}>
		<h2 className={classes.h2}>About Us</h2>
					<p><a className={classes.a} href='http://localhost:3000/About'>Parks2Go</a> ipsum dolor sit amet. The hotel, the casino. The Corleone Family wants to buy you out. I donât like violence, Tom. Iâm a businessman; blood is a big expense.</p>
        </Grid>

        <Grid item xs={12} md={8}>
			<h2 className={classes.states}>Parks2Go</h2>
	  		<States style={{fontSize:'smaller'}}/>
	  	</Grid>

      <Grid item xs={12} md={1}>
	  <h2 className={classes.connect}>Connect</h2>
	  <GitHub className={classes.socialMedia}/>
	  <LinkedIn className={classes.socialMedia}/>
	  <Youtube className={classes.socialMedia} />
      </Grid>

		<Grid item xs={12} md={12}><Copyright /></Grid>
      </Grid>
    </div>
  );
}
