		.then(results =>{
		return results.json();
	}).then(data =>{

		let mList = data.data.map((use, i)=>{

			{use.images.map(p => {
 				return (<Grid item xs={12} md={4} style={styles.moreImages}>				
				{use.images.map(p => {
 					return (<img key={p} src={p.url} alt={p.altText} style={{width: 80, height: 80, borderRadius: 80/ 2, padding: '1%'}}/>
									)})}
						</Grid>
									)})}

		})

		this.setState({imgGallery:mList});
	})
}

