          <Paper className={classes.paper} variant="outlined">
		<h2 className={classes.h2}>Parks2Go</h2>

	  <Grid item xs={4}>
          <p><a className={classes.a} href='https://reactjs.org'>Company</a></p>
        </Grid>
        <Grid item xs={4}>
