import React from 'react';
import ImageGallery from 'react-image-gallery';
import Grid from "@material-ui/core/Grid";

//Smart Component
class ImgGallery extends React.Component {
	//declaring state and new object
	state ={
		imgListing: [],
	}

//calling fetchData function
	componentDidMount(){
	this.fetchData();
}

//fetch Api data and map json results in a list format
fetchData(){
	const urlArray=[];
	const urlArray2=[];

 fetch('https://developer.nps.gov/api/v1/parks?statecode=FL&api_key=YpbDDtsNwQRi13JXZXiN7DnEIusWnKQLsCZW11xq')
		.then(results =>{
		return results.json();
	}
	).then(data =>{
let mList = data.data.map((use, i)=>{
	return(
		<Grid key={i} item xs={12} md={11} style={styles.moreImages}>				
		{use.images.map(p => {
		let url = p.url;
		let urlString = url.toString();
		console.log(urlString);	
		var res = urlString.split("https://www.nps.gov/common/uploads/structured_data/");
			console.log(res);
			urlArray.push(res);
			console.log(urlArray.length);

		for( var i = 0; i < urlArray.length; i++)
		{
			console.log(urlArray[i]);
		}

		var url2 = urlArray.toString();
		var res2 = url2.split(",");

	for( var i = 0; i < res2.length; i++)
	{ if ( res2[i] === "") { res2.splice(i, 1); }}
	console.log(res2);
	const images2 = [];

	for( var i = 0; i < res2.length; i++)
		{
	 images2.push({
    original: `https://www.nps.gov/common/uploads/structured_data/${res2[i]}`,
    thumbnail: `https://www.nps.gov/common/uploads/structured_data/${res2[i]}`,
  },)
	}
	for( var i = 0; i < images2.length; i++)
		{
			console.log(images2[i]);
		}


 					return (<li key={p} style={styles.indImgs}><img src={p.url} alt={p.altText} style={{width: 80, height: 80, borderRadius: 80/ 2, padding: '1%'}}/></li>
									)})}
						</Grid>

			)
		})
		this.setState({ImgGallery:mList});
	})
}



	 render() { 
  return (
	  <div>
	  <Grid container style={styles.container}>
	  <Grid item xs={12}>



	  </Grid>
	  </Grid>
	  {this.state.ImgGallery}
		</div>			
  	);
  }
}

export default ImgGallery

