// JavaScript Document
import React from 'react';
import StateActivities from '../pages/stateActivities';
import ParkGrid from '../pages/parkGrid';

//Material UI
import Typography from '@material-ui/core/Typography';
import Link from '@material-ui/core/Link';
import Grid from "@material-ui/core/Grid";
import Box from '@material-ui/core/Box';

const id ='FL';
const endpoint = `stateCode=${id}&limit=2`;

//Smart Component
class StateParks extends React.Component {
	//declaring state and new object
	state ={
		parkListing: [],
	}

//calling fetchData function
	componentDidMount(){
	this.fetchParks();
}

//fetching API
 fetchParks(){
	fetch(`https://developer.nps.gov/api/v1/parks?${endpoint}&api_key=YpbDDtsNwQRi13JXZXiN7DnEIusWnKQLsCZW11xq`)
		.then(results =>{
		return results.json();
	}).then(data =>{

		let mList = data.data.map((use, i)=>{
			return(


	 <Typography display="block" variant="body" align="left">
	  <Box component="li" display="block" p={2} m={1}>
      	<Link color="inherit" href="https://www.huffpost.com/entry/why-do-people-blog-the-be_b_8178624">
		<img src={use.images[0].url} alt={use.images[0].altText} style={{width: '25%', borderRadius: 50, padding: '2%'}}/>
				</Link>
	  	{' '}
	  </Box>
    </Typography>

			)
		})

		this.setState({StateParks:mList});
	})
} 
	 render() { 
  return (
  <div>
	  <Grid container style={styles.container}>
	  	<Grid item xs={12} style={styles.topRow}>
			<h1> State National Parks</h1>
	  <Grid item xs={12} style={styles.images}>
	  		<ParkGrid />
	  </Grid> 
	   	</Grid> 
	 	<Grid item xs={12} style={styles.bottomRow}>
		<StateActivities />
	   	</Grid> 
	   </Grid>  	
	  {this.state.StateParks}		
	</div>
  );
 }
}
export default StateParks

const styles ={
	container:{

	},
	images:{
		columns:'4 auto',
	}
}
