// JavaScript Document
import React from 'react';
import Inprogress from '../progress_indicator/inProgress'

//Material UI
import {withStyles} from '@material-ui/styles';
import GridList from '@material-ui/core/GridList';
import Grid from '@material-ui/core/Grid';
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';

const style = withStyles =>({
root:{
	direction:'column',
	flexGrow:1,
	margin:'1%',
	},
container:{
    display: 'flex',
	 flexWrap: 'wrap',
	justifyContent: 'space-around',
	overflow: 'hidden',
	},
gridList: {
	paddingBottom:'0.2%',
  },
paper: { 
    textAlign: 'center',
		width:'400px',
		height: '400px',
		listStyleType:'none',
	backgroundColor:'white',
  },
indImgs:{
	height: '100%',
	width:'100%',
	},
});


 let sCode = window.location.pathname;
 let newSC = sCode.split('/stateActivities/');
 newSC.shift();

const targetId = newSC;
const endpoint = `statecode=${targetId}&limit=2`;


//Smart Component
class ParkGridStateParks extends React.Component {
	//declaring state and new object
	state ={
		parkArray: [],
		isLoading: false,
	}

parkClick(event) {
	window.onclick= event => {
		//console.log(event.target);

		var parkId = event.target.id;
		window.location.assign(`http://localhost:3000/park/${parkId}`);
	};
} 

//calling fetchData function
	componentDidMount(){
	this.fetchData();
}

//fetching API
fetchData(){
	 const { classes } = this.props;
	this.setState({ isLoading: true }, () => {

	fetch(`https://developer.nps.gov/api/v1/parks?${endpoint}&api_key=YpbDDtsNwQRi13JXZXiN7DnEIusWnKQLsCZW11xq`)
		.then(results =>{
		return results.json();
	}).then(data => {this.setState({
		 isLoading:false,
		data:data.data
		})

					 console.log('data',data)
					})
		//console.log(data)
		//console.log("state", this.state.data)
	})
}		   
	 render() { 
		 const { classes } = this.props;
		 const { data, isLoading } = this.state; 
		 	console.log(isLoading)

		 if(isLoading){
            return <Inprogress />
          }

			let newArray = data;
			 console.log(newArray)

			if (newArray != null) {
        		return (
            		 <GridList cellHeight={400} cellWidth={500} ClassName={classes.gridList}>
                		{newArray.map((use =>
							<GridListTile cols={1} ClassName={classes.paper}>
								//{console.log(use)}
								<img key={use.images[0].id} id={use.parkCode} src={use.images[0].url} alt={use.images[0].altText} ClassName={classes.indImgs} onClick={this.parkClick}/>
								<GridListTileBar title={use.images[0].title} subtitle={<span>{use.addresses[0].city}, {use.addresses[0].stateCode}</span>} />

							</GridListTile>	
							))
						}
            		</GridList>
        		);
    }
  return (
 <div ClassName={classes.root}>
      <Grid container ClassName={classes.container}>
	  		{this.state.ParkGridStateParks}	
     </Grid>
    </div>
  );
 }
}
export default withStyles(styles)(ParkGridStateParks);
