// JavaScript Document
import React from 'react';

import './Modal.css';

const modal = (props) => {
    return (
        <div>
            <div style={styles.container}
                style={{
                    transform: props.show ? 'translateY(0vh)' : 'translateY(-100vh)',
                    opacity: props.show ? '1' : '0'
                }}>
                <div style={styles.modalHeader}>
                    <h3 style={styles.h3}>Send Us An Email</h3>
                    <span style={styles.closeBtn} onClick={props.close}>Ã—</span>
                </div>
                <div style={styles.modalBody}>
                    <p>
                        {props.children}
                    </p>
                </div>
                <div style={styles.modalFooter}>
                    <button style={styles.btnCancel} onClick={props.close}>CLOSE</button>
                    <button style={styles.btnContinue}>CONTINUE</button>
                </div>
            </div>
        </div>
    )
}

export default modal;

const styles ={
	container:{
    background: 'white',
    border: '1px solid #d0cccc',
    margin: '0',
    transition:'.8s',
    width: '60%',
},
modalHeader:{
    background: '#263238',
    height: '40px',
    padding: '5px 20px',
    textAlign: 'right',
},
h3:{
    color: 'white',
    float: 'left',
    margin: '0',
    padding: '0',
},
modalBody:{
    padding: '10px 15px',
     textAlign: 'Center',
},
modalFooter:{
    background: '#263238',
    height: '35px',
    padding: '15px',
},
closeBtn:{
    color: 'white',
    cursor: 'pointer',
    float: 'right',
    fontSize: '30px',
    margin: '0',
},
btnContinue:{
    background: 'coral',
    border: 'none',
    color: 'white',
    cursor: 'pointer',
    fontWeight: 'bold',
    outline: 'none',
    padding: '10px',
	backgroundColor: '#1b5e20',
    float: 'right',
},
btnCancel:{
	 background: 'coral',
    border: 'none',
    color: 'white',
    cursor: 'pointer',
    fontWeight: 'bold',
    outline: 'none',
    padding: '10px',
    backgroundColor: '#b71c1c',
    float: 'left',
},
backDrop:{
    backgroundColor: 'rgba(48, 49, 48, 0.42)',
    height: '100%',
    position: 'fixed',
    transition: 'all 1.3s',
    width: '100%',
},
openModalBtn:{
    margin: '15px',
    padding: '10px',
    fontWeight: 'bold',
},
}
