import React, { Component } from 'react';
//import './App.css'; /* optional for styling like the :hover pseudo-class */
import StateParks from '../pages/StateParks';

//Material UI
import Typography from '@material-ui/core/Typography';
import Link from '@material-ui/core/Link';
import USAMap from "react-usa-map";

class App extends Component {
  /* mandatory */
  mapHandler = (event) => {
    //alert(event.target.dataset.name);
	  var str = "event.target.dataset.name";
  		var result = str.link("http://localhost:3000/StateParks");
	  //<a href="http://localhost:3000/StateParks" id={event.target.dataset.name} runat="server" >event.target.dataset.name</a>
  };

  /* optional customization of filling per state and calling custom callbacks per state */
  statesCustomConfig = () => {
    return {
      "NJ": {
        fill: "navy",
        //clickHandler: (event) => console.log('Custom handler for NJ', event.target.dataset)
		  clickHandler: (event) => <a href="http://localhost:3000/StateParks" id={event.target.dataset.name} runat="server" >event.target.dataset.name</a>

      },
      "NY": {
        fill: "#CC0000"
      }
    };
  };

  render() {
    return (
      <div className="App">
        <USAMap customize={this.statesCustomConfig()} onClick={this.mapHandler} />
      </div>
    );
  }
}

export default App;
