// JavaScript Document
import React from 'react';
import Fetch from '../components/Fetch';

//Material UI
import Typography from '@material-ui/core/Typography';
import Link from '@material-ui/core/Link';
import Grid from "@material-ui/core/Grid";

//Images & Icons
import image from '../images/placeholder.jpg'

import { createMuiTheme } from "@material-ui/core/styles";
const theme = createMuiTheme({
	  typography: {
    fontFamily: 'Lora',
  },
	palette: {
    primary: {
      light: '#B9B9B9',
      main: '#000000',
    },
    secondary: {
      main: '#ea7a0f',
      contrastText: '#ffffff',
    },
  },
});

//Smart Component
class Parks extends React.Component {
	//declaring state and new object
	state ={
		parkListing: [],
		activityListing:[],
	}

//calling fetchData function
	componentDidMount(){
	this.fetchParks();
		this.fetchActivities();
}

//fetching API
 fetchParks(){

	//state= x;

	fetch('https://developer.nps.gov/api/v1/parks?statecode=FL&api_key=YpbDDtsNwQRi13JXZXiN7DnEIusWnKQLsCZW11xq')
		.then(results =>{
		return results.json();
	}).then(data =>{

		let pList = data.data.map((use)=>{
			return(

			  <li key={this.props.id}>
				<span key={use.results}>  
     <Typography variant="body" align="left">
      	<Link color="inherit" href="/">
		<img src={use.images[0].url} alt={use.images[0].altText} width="275" height="175"/><br />
		{use.fullName}
</Link>{' '}
</Typography>
				</span>
				</li>
			)
		})

		this.setState({parks:pList});
	})
}

 fetchActivities(){
	fetch('https://developer.nps.gov/api/v1/activities?statecode=FL&results=1&api_key=YpbDDtsNwQRi13JXZXiN7DnEIusWnKQLsCZW11xq')
		.then(results =>{
		return results.json();
	}).then(data =>{

		let aList = data.data.map((use)=>{
			return(

			  <li key={this.props.id}>
				<span key={use.results}>  
     <Typography variant="body" align="left">
				<p><b>Park Activities</b>
      	<Link color="inherit" href="/">

					{use.activities.map(item =>{
						return(
							<div>{item.name}</div>	
								)
							})
						}

</Link>{' '}
			</p>
</Typography>

				</span>
				</li>
			)
		})

		this.setState({parks:aList});
	})
}
	 render() { 
  return (
  <Grid container style={styles.container}>
	   <Grid item xs={12} md={1} style={styles.links}>
	  <Link />
	   </Grid>
	  {this.state.parks}		
	  </Grid>

  );
 }
}
export default Parks

const styles ={
	container:{
		marginTop:'1%',
		border:'1px solid red',
	},
	h1:{
		textAlign:'center',
		fontSize:'3em',
		color: theme.palette.secondary.main

	},
	pImg:{
		textAlign:'right',
		width:'100%',
	},
	proImg:{
		textAlign:'right',
		marginTop:'1%',
	},
	proInfo:{
		marginTop:'-1%',
		textAlign:'left',
		color:theme.palette.secondary.main,
		paddingBottom:'5%',
	},
}
