// JavaScript Document
import React from 'react';
import ImgGallery from '../pages/ImgGallery';
import ThumbnailGallery from '../components/thumbnailGallery/thumbnailGallery';
import ActiveThumbnailWindow from '../components/thumbnailGallery/active-thumbnail-window';
import ThumbnailGrid from '../components/thumbnailGallery/thumbnail-grid';

//Material UI
import Grid from "@material-ui/core/Grid";

//Images & Icons
import imgUrl from '../images/parkBackground.png';
import Print from '@material-ui/icons/Print';

import { createMuiTheme } from "@material-ui/core/styles";

let pCode = window.location.pathname;

let newPC = pCode.split('/Park/');
 newPC.shift();

const parkId = newPC;

//const parkId ='bepa';
const endpoint = `parkCode=${parkId}`;

const theme = createMuiTheme({
	  typography: {
    fontFamily: 'Lora',
  },
	palette: {
    primary: {
      light: '#B9B9B9',
      main: '#000000',
    },
    secondary: {
      main: '#ea7a0f',
      contrastText: '#ffffff',
    },
  },
});


//Smart Component
class Park extends React.Component {
	//declaring state and new object
	state ={
		imageArray: [],
		activeIndex: 0
	}

//calling fetchData function
	componentDidMount(){
	this.fetchData();
}

//fetch Api data and map json results in a list format
fetchData(){
	fetch(`https://developer.nps.gov/api/v1/parks?${endpoint}&api_key=YpbDDtsNwQRi13JXZXiN7DnEIusWnKQLsCZW11xq`)
		.then(results =>{
		return results.json();
	}).then(data =>{

		let mList = data.data.map((use, i)=>{
			console.log(data.data[i].images);
			this.setState({imageArray:data.data[i].images})
		})
	})
}

activeThumbnail = () => {
	const {imageArray, activeIndex} = this.state
	if(imageArray.length){
		return(
				<ActiveThumbnailWindow 
			activeThumbnail={imageArray[activeIndex]}
			/>
		)
	}
	 return null
 }

 handleClick =(e) => {
const newActiveIndex = e.target.getAttribute('imgindex')
console.log(newActiveIndex);
	 this.setState({activeIndex: newActiveIndex})
 }

	 render() { 
  return (
	  <div style={styles.container}>
	  <Grid container style={styles.container}>
	  				<Grid item xs={12} md={8} style={styles.leftCol}>
{/*Park Images*/}	
	  					<Grid item xs={11} style={styles.imgs}>
							const {imageArray} = this.state
								return(			
									<div style={styles.thumbnailGalleryStyles}> 
										<div style={{flex: 1}}>
											{this.activeThumbnail()}
											<ThumbnailGrid  thumbnails={imageArray} handleClick={this.handleClick}/>
								  		</div>
									</div>
									)
	   					</Grid> 

{/*Park Activities*/}
					<Grid item xs={12} style={styles.indentRight}>
		  				<h2>Park Activities</h2>
	  					<Grid item xs={11} style={styles.activities}>
							<ul>
							{use.activities.map(activity =>{
							return(
							<li key={activity.name} style={{backgroundColor: 'rgb(15,15,15,0.5)'}}>{activity.name}</li>	
							)})}
							</ul>
	  					</Grid>
	   				</Grid> 
{/*Park Topics*/}
	 				<Grid item xs={12} style={styles.indentRight}>
		  				<h2>Park Topics</h2>

	  					<Grid item xs={11} style={styles.topics}>
							<ul>
							{use.topics.map(topic =>{
								return(
								<li key={topic.name} style={{backgroundColor: 'rgb(15,15,15,0.5)'}}>{topic.name}</li>	
								)})}
								</ul>
	  					</Grid>

					</Grid> 
{/*Weather*/}
					<Grid item xs={11} style={styles.indentRight}>
		  				<h2>Weather Information</h2>
	  					<p>{use.weatherInfo}</p>
	   				</Grid> 
	   			</Grid> 
	 		<Grid item xs={12} md={4}style={styles.rightCol}>
				<Grid container>
					<Grid item xs={12} style={styles.h2}>
						<Grid container>
							<Grid item xs={12} style={styles.printer}>
								<Print size={25}/>
	  						</Grid> 
	  						<Grid item xs={12} style={styles.h2}>
								<h1>{use.name}</h1>
								<h3 style={styles.moveUp}>{use.addresses[0].line1}, {use.addresses[0].stateCode}<br />
								{use.addresses[0].city}, {use.addresses[0].stateCode} {use.addresses[0].postalCode}</h3>
							</Grid> 
	  					</Grid> 
	   			</Grid> 
{/*Park Description*/}
	 			<Grid item xs={12} style={styles.description}>
		  				<h2>Park Description</h2>
	  					<p style={styles.moveUp}>{use.description}</p>
	   			</Grid>
{/*Park Details*/}
				<Grid item xs={12} style={styles.details}>
		  				<h2>Park Details</h2>
					    <p style={styles.moveUp}><b>{use.entranceFees[0].description}</b><br />
					    <b>Cost: </b>{use.entranceFees[0].cost}</p>
	   			</Grid>
{/*Park Directions*/}
	 			<Grid item xs={12} style={styles.directions}>
		  				<h2>Park Directions</h2>
						<p style={styles.moveUp}>{use.directionsInfo}</p>
	   			</Grid>
			</Grid>  
	   	</Grid>
	</Grid>  	

	  {this.state.park}
		</div>			
  	);
  }
}

export default Park

const styles ={
	container:{
		backgroundImage: 'url(' + imgUrl + ')',	
		marginBottom:'-3%',
		paddingBottom:'5%',
		paddingRight:'3%',
	},
	imgs:{
		backgroundColor:'rgb(15,15,15,0.7)',
	},
	moreImages:{
		backgroundColor:'rgb(15,15,15,0.7)',
		borderBottomRightRadius: 30,
		marginBottom:'10%',
		padding:'2%',
		columns: '5 auto',
	},
	indImgs:{
		marginRight:'-5%',
		listStyleType:'none',
	},
rightCol:{
	color:'white',
},
	leftCol:{
	color:'white',
},
	printer:{
		marginTop:'7%',
		textAlign:'right',
	},
	moveUp:{
		marginTop:'-4%',
	},
	activities:{
		columns: '3 auto',
		marginBottom:'10%',
		listStyleType:'none',
	},
	topics:{
		columns: '3 auto',
		listStyleType:'none',
		marginBottom:'10%',
	},
	indentRight:{
		paddingLeft:'5%',
	},
	description:{
		marginBottom:'8%',
		marginTop:'10%',
	},
	details:{
		marginBottom:'6%',
	},
	directions:{
		marginBottom:'6%',
	},
}
