// JavaScript Document
import React from 'react';

//Material UI
import {withStyles} from '@material-ui/styles';
import GridList from '@material-ui/core/GridList';
import Grid from '@material-ui/core/Grid';
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';

 const styles = theme => ({
  root:{
	direction:'row',
	flexGrow:'1',
	margin:'1%',
	},
container:{
    display: 'flex',
	 flexWrap: 'wrap',
	justifyContent: 'space-around',
	overflow: 'hidden',
	},
gridList: {
	padding:'0.4%',
  },
paper: { 
    textAlign: 'center',
		width:'400px',
		height: '300px',
		listStyleType:'none',
		backgroundColor:'white',
  },
indImgs:{
	height: '100%',
	width:'100%',
	},
});

const endpoint = 'limit=3';

//Smart Component
class ParkGridHome extends React.Component {
	//declaring state and new object
	state ={
		imageListing: []
	}

parkClick(event) {
	window.onclick= event => {
		//console.log(event.target);

		var parkId = event.target.id;
		window.location.assign(`http://localhost:3000/park/${parkId}`);
	};
} 

//calling fetchData function
	componentDidMount(){
	this.fetchData();
}

//fetching API
 fetchData(){

	fetch(`https://developer.nps.gov/api/v1/parks?${endpoint}&api_key=YpbDDtsNwQRi13JXZXiN7DnEIusWnKQLsCZW11xq`)
		.then(results =>{
		return results.json();
	}).then(data =>{

		let mList = data.data.map((use, i)=>{
			const  classes  = styles();
			return(

				<GridList key={i} className={classes.gridList}>
				<GridListTile className={styles("paper")}>
					<img id={use.parkCode} src={use.images[0].url} alt={use.images[0].altText} className={classes.indImgs} onClick={this.parkClick} />
				<GridListTileBar title={use.images[0].title} subtitle={<span>{use.addresses[0].city}, {use.addresses[0].stateCode}</span>} />
				</GridListTile>	
				</GridList>

			)
		})

		this.setState({ParkGridHome:mList});
	})
}
	 render() { 
		const  classes  = styles();
  return (

      <div className={classes.root}>
				<Grid container className={classes.container}>
	  		{this.state.ParkGridHome}	

    </Grid>
				</div>
  );
 }
}
export default withStyles(styles)(ParkGridHome);

