// JavaScript Document
import React, {Component} from 'react';
import ActiveThumbnailWindow from './active-thumbnail-window'
import ThumbnailGrid from './thumbnail-grid'

const id ='bepa';
const endpoint = `parkCode=${id}`;

class ImageGallery extends React.Component {
	state ={
		imageArray: [],
		activeIndex: 0
	}

 activeThumbnail = () => {
	const {imageArray, activeIndex} = this.state
	if(imageArray.length){
		return(
				<ActiveThumbnailWindow 
			activeThumbnail={imageArray[activeIndex]}
			/>
		)
	}
	 return null
 }

 handleClick =(e) => {
const newActiveIndex = e.target.getAttribute('imgindex')
console.log(newActiveIndex);
	 this.setState({activeIndex: newActiveIndex})
 }
	render(){
		const {imageArray} = this.state
		return(

			<div style={styles.container}> 

			<div style={{flex: 1}}>
			{this.activeThumbnail()}
			<ThumbnailGrid 
				thumbnails={imageArray} handleClick={this.handleClick}/>
			</div>

			</div>
		)
	}
}
export default ImageGallery

const styles ={
container : {
	background: '#ddd',
	height: '385px',
	width: '500px',
	margin:'40px auto',
	display:'flex',
},
}
