// JavaScript Document
import React from 'react';

//Material UI
import Box from '@material-ui/core/Box';
import Grid from "@material-ui/core/Grid";

 let sCode = window.location.pathname;
 let newSC = sCode.split('/StateParks/');
 newSC.shift();

const targetId = newSC;
const endpoint = `stateCode=${targetId}&limit=2`;

//Smart Component
class ParkGridStateParks extends React.Component {
	//declaring state and new object
	state ={
		parkListing: [],
	}

//calling fetchData function
	componentDidMount(){
	this.fetchParks();
}

//fetching API
 fetchParks(){
	fetch(`https://developer.nps.gov/api/v1/parks?${endpoint}&api_key=YpbDDtsNwQRi13JXZXiN7DnEIusWnKQLsCZW11xq`)
		.then(results =>{
		return results.json();
	}).then(data =>{

		let mList = data.data.map((use, i)=>{
			return(
<Box>
				<img src={use.images[1].url} alt={use.images[1].altText} style={{width: '100%'}}/>
</Box>
			)
		})
		this.setState({ParkGridStateParks:mList});
	})
} 
 mapHandler = (event) => {
	  //var targetId = event.target.dataset.name;
	alert(window.location.pathname);
	  //window.location.assign(`http://localhost:3000/StateParks/${targetId}`);
  };
	 render() { 
  return (
  	<div> 
	  <Grid container >
	  	 <Grid item xs={12}>
	  		<h1 style={styles.h1}>{targetId} State National Parks</h1>
	  	</Grid> 
	  	<Grid item xs={12}>
	  				<Box onClick={this.mapHandler}/>
	  			</Grid> 
	 		</Grid>

	  {this.state.ParkGridStateParks}		
	</div>
  );
 }
}
export default ParkGridStateParks
const styles ={
	h1:{
		textAlign:'center',
	},
}
