// JavaScript Document
import React from 'react';

//Material UI
import Box from '@material-ui/core/Box';
import Grid from "@material-ui/core/Grid";

 let sCode = window.location.pathname;
 let newSC = sCode.split('/StateParks/');
 newSC.shift();

const targetId = newSC;
const endpoint = `stateCode=${targetId}&limit=2`;

//Smart Component
class ParkGridStateParks extends React.Component {

	//declaring state and new object
	state ={
		parkListing: [],
	}

parkHandler = (event) => {
	  var parkId = event.target.dataset.value;
  alert(parkId);
	  //window.location.assign(`http://localhost:3000/StateParks/${targetId}`);
  };

//calling fetchData function
	componentDidMount(){
	this.fetchParks();
}

//fetching API
 fetchParks(){
	fetch(`https://developer.nps.gov/api/v1/parks?${endpoint}&api_key=YpbDDtsNwQRi13JXZXiN7DnEIusWnKQLsCZW11xq`)
		.then(results =>{
		return results.json();
	}).then(data =>{

		let mList = data.data.map((use, i)=>{
			var parkId = use.parkCode
			console.log(parkId);
			return(
				<Box key={i} >
				<img src={use.images[1].url} alt={use.images[1].altText} style={{width:'100%'}}/>
				<h2 style={styles.h1}>{use.name}</h2>
				<h3 style={styles.h3}>{use.addresses[1].city}, {use.addresses[1].stateCode}</h3>
				</Box>
			)
		})
		this.setState({ParkGridStateParks:mList});
	})
} 
	 render() { 
  return (
  	<div> 
	  <Grid container >
	  	 <Grid item xs={12}>
	  		<h1 style={styles.h1}>{targetId} State National Parks</h1>
	  	</Grid> 
	  	<Grid item xs={12}>
	  				<Box onClick={this.parkHandler}/>
	  			</Grid> 
	 		</Grid>

	  {this.state.ParkGridStateParks}		
	</div>
  );
 }
}
export default ParkGridStateParks
const styles ={
	h1:{
		textAlign:'center',
	},
	h2:{
		marginTop:'-11%',
		color:'white',
	},
	h3:{
		marginTop:'-3%',
		color:'white',
	},
}
