// JavaScript Document
import React, {Component} from 'react';
import LargeImage from './largeImage'
import ThumbnailGrid from './thumbnail-grid'

//Material UI
import Grid from "@material-ui/core/Grid";

const id ='bepa';
const endpoint = `parkCode=${id}`;

class ImageGallery extends React.Component {
	state ={
		imageArray: [],
		imgIndex: 0
	}

//calling fetchData function
	componentDidMount(){
	this.fetchData();
	}

	//fetch Api data and map json results in a list format
	fetchData(){
		fetch(`https://developer.nps.gov/api/v1/parks?${endpoint}&api_key=YpbDDtsNwQRi13JXZXiN7DnEIusWnKQLsCZW11xq`)
			.then(results =>{return results.json();}
		).then(data =>{data.data.forEach((use, i)=>{	
			this.setState({imageArray:data.data[i].images})

			})
		})
	}

 lgImage = () => {
	const {imageArray, imgIndex} = this.state
	if(imageArray.length){
		return(
				<LargeImage 
			lgImage={imageArray[imgIndex]}
			/>
		)
	}
	 return null
 }

 handleClick =(e) => {
const imageSelection = e.target.getAttribute('imgindex')
	 this.setState({imgIndex: imageSelection})
 }
	render(){
		const {imageArray} = this.state
		return(
			<Grid container style={styles.container}> 
			<Grid item xs={11} style={styles.imgs}>
				<div style={{flex: 1}}>
					{this.lgImage()}
					<ThumbnailGrid thumbnails={imageArray} handleClick={this.handleClick}/>
				</div>
				</Grid>
			</Grid>
		)
	}
}
export default ImageGallery

const styles ={
container : {
	height: '385px',
	width: '500px',
	margin:'40px auto',
	display:'flex',
},
}
