fetchData(){
	fetch('https://developer.nps.gov/api/v1/parks?parkCode=bepa&api_key=YpbDDtsNwQRi13JXZXiN7DnEIusWnKQLsCZW11xq')
		.then(results =>{
		return results.json();
	}).then(data =>{

		let mList = data.data.map((use)=>{
			return(
				<div key={this.props.id}>
				<span key={use.results}>

				<Grid container style={styles.container}>
	  				<Grid item xs={12} md={7} style={styles.leftCol}>
	  					<Grid item xs={12} md={11} style={styles.images}>		
		  					<img src={use.images[0].url} alt={use.images[0].altText} style={{width: '95%', borderRadius: 50, padding: '2%'}}/>
	   					</Grid> 
	 				<Grid item xs={12} md={11} style={styles.moreImages}>
						<ul>
						{use.images.map(p => {
                  			return (<li style={styles.indImgs}><img key={p} src={p.url} alt={p.altText} style={{width: 130, height: 130, borderRadius: 130/ 2, padding: '1%'}}/></li>
							)})}
						</ul>
	   				</Grid>
					<Grid item xs={12}>
		  				<h2 style={styles.moveRight}>Park Activities</h2>
	  					<ul>
	  					<Grid item xs={11} style={styles.activity}>
							{use.activities.map(item =>{
							return(
							<li key={item}>{item.name}</li>	
							)})}
	  					</Grid>
						</ul>
	   				</Grid> 
	 				<Grid item xs={12}>
		  				<h2 style={styles.moveRight}>Park Topics</h2>
						<ul>
	  					<Grid item xs={11} style={styles.topic}>
							{use.topics.map(item =>{
								return(
								<li key={item}>{item.name}</li>	
								)})}
	  					</Grid>
						</ul>
					</Grid>  
	   			</Grid> 
	 		<Grid item xs={12} md={4}style={styles.rightCol}>
				<Grid container>
					<Grid item xs={12} style={styles.h2}>
						<Grid container>
							<Grid item xs={12} style={styles.printer}>
								<Print size={25}/>
	  						</Grid> 
	  						<Grid item xs={12} style={styles.h2}>
								<h1>{use.name}</h1>
								<h3 style={styles.h3}>{use.addresses[0].city}, {use.addresses[0].stateCode}</h3>
							</Grid> 
	  					</Grid> 
	   			</Grid> 
	 			<Grid item xs={12} style={styles.details}>
		  				<h2>Park Details</h2>
					    <p><b>Cost: </b>{use.entranceFees[0].cost}</p>
					    <p><b>Description: </b>{use.entranceFees[0].description}</p>
					    <p><b>Title: </b>{use.entranceFees[0].title}</p>
	   			</Grid>
				<Grid item xs={12} style={styles.description}>
		  				<h2>Park Description</h2>
	  					<p>{use.description}</p>
	   			</Grid> 
	 			<Grid item xs={12} style={styles.directions}>
		  				<h1><a>Directions</a></h1>
	   			</Grid>
			</Grid>  
	   	</Grid>
	</Grid>  	
</span>
				</div>
			)
		})

		this.setState({park:mList});
	})
}

	 render() { 
  return (
	  <div style={styles.container}>
	  {this.state.park}
		</div>			
  	);
  }
}

export default Park

