// JavaScript Document
import React from 'react';

//Material UI
import Grid from "@material-ui/core/Grid";
import GridList from '@material-ui/core/GridList';
import Box from '@material-ui/core/Box';
import makeStyles from '@material-ui/core/styles';

//styles
const useStyles = makeStyles(theme => ({
item:{
	columns:'3',
	margin:'1%'
	},
li:{
	listStyleType:'none',	
	},
indImgs:{
	height: '100%',
	width:'100%',
	marginBottom:'5%',
	},
h2:{
	marginTop:'-20%',
	paddingLeft:'3%',
	color:'white',
	},
h5:{
	marginTop:'-6%',
	paddingLeft:'3%',
	color:'white',
	},
}));

//console.log(window.location.pathname);
let sCode = window.location.pathname;
 let newSC = sCode.split('/stateParks/');
 newSC.shift();
const parkId = newSC;

const activity = 'artsandculture'
const targetId = newSC;
const endpoint = `statecode=${targetId}`;

//Smart Component
class StateParks extends React.Component {
	//declaring state and new object
	state ={
		picListing: []
	}

parkClick(event) {
	window.onclick= event => {
		//console.log(event.target);
		//console.log(event.target.id);

		var parkId = event.target.id;
		window.location.assign(`http://localhost:3000/park/${parkId}`);
	};
} 

//calling fetchData function
	componentDidMount(){
	this.fetchData();
}

filterByActivity(current_value, i){

			for(let i=0; i< current_value.activities.length;i++ ) {
				let activity = current_value.activities[i];
				if(activity.id =='7CE6E935-F839-4FEC-A63E-052B1DEF39D2')
					return true;
			}
			console.log(current_value);
		return false;
}

//fetching API
 fetchData(){
	 const classes = useStyles();
	fetch(`https://developer.nps.gov/api/v1/parks?${endpoint}&api_key=YpbDDtsNwQRi13JXZXiN7DnEIusWnKQLsCZW11xq`)
		.then(results =>{
		return results.json();
	}).then(data =>{

		let filteredArray = data.data.filter(this.filterByActivity);

		let mList = filteredArray.map((use, i)=>{
			return(	


				<GridListTile key={use.id} cols={use.title || 1}>
				<img id={use.parkCode} src={use.images[0].url} alt={use.images[0].altText} className={classes.indImgs} onClick={this.parkClick}/>

				</GridListTile>


			)
		})

		this.setState({StateParks:mList});
	})
}
	 render() { 

  return (
	  <Grid container>
	   <Grid item xs={12} className={classes.item}>
	  <GridList cellHeight={auto} className={classes.gridList} cols={3}>

	  {this.state.StateParks}

  	</GridList>
	  </Grid>
       </Grid>	

  );
 }
}
export default StateParks
