// JavaScript Document
import React from 'react';
import Inprogress from '../components/progress_indicator/inProgress'

//Material UI
import GridList from '@material-ui/core/GridList';
import Grid from '@material-ui/core/Grid';
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';

const useStyles ={
/*root:{
	direction:'row',
	flexGrow:1,
	},
container:{
    display: 'flex',
	 flexWrap: 'wrap',
	overflow: 'hidden',
	},
gridList: {
	padding:'0.1%',
  },
paper: { 
    textAlign: 'center',
		width:'482px',
		height: '382px',
		listStyleType:'none',
		backgroundColor:'white',
	//[theme.breakpoints.up('md')]: {
    //width:'400px',
	//height: '300px',
	//},
  },
indImgs:{
	height: '100%',
	width:'100%',
	},*/
};

let stCode = window.location.pathname;
let newStC = stCode.split('/');

var actCd = newStC.slice(3);

var stateCd = newStC.slice(2,3);

const activityId = actCd;
const targetId = stateCd;
const endpoint = `statecode=${targetId}`;

//Smart Component
class StateParks extends React.Component {
	//declaring state and new object
	state ={
		picListing: [],
		isLoading: false,
	}

parkClick(event) {
	window.onclick= event => {
		//console.log(event.target);

		var parkId = event.target.id;
		window.location.assign(`http://localhost:3000/park/${parkId}`);
	};
} 

//calling fetchData function
	componentDidMount(){
	this.fetchData();
}

filterByActivity(current_value, i){

			for(let i=0; i< current_value.activities.length;i++ ) {
				let activity = current_value.activities[i];
				if(activity.id === `${activityId}`)
					return true;
			}
		return false;
}

//fetching API
 fetchData(props){

	this.setState({ isLoading: true }, () => {
	fetch(`https://developer.nps.gov/api/v1/parks?${endpoint}&api_key=YpbDDtsNwQRi13JXZXiN7DnEIusWnKQLsCZW11xq`)
		.then(results =>{
		return results.json();
	}).then(data =>{this.setState({
				isLoading:false,
				data:data.data
			})

		let filteredArray = data.data.filter(this.filterByActivity);

		let mList = filteredArray.map((use, i)=>{
			const  {classes}  = this.props;
			return(
				<div className={classes.root}>
				<GridList key={i} cellHeight={200} className={classes.gridList} style={{ padding:'0.1%', }}>
				<GridListTile col={1} style={{ width:'430px', height: '425px' }}>
				<img id={use.parkCode} src={use.images[0].url} alt={use.images[0].altText} className={classes.indImgs} onClick={this.parkClick}/>
				<GridListTileBar title={use.images[0].title} subtitle={<span>{use.addresses[0].city}, {use.addresses[0].stateCode}</span>} />
				</GridListTile>	
				</GridList>
				</div>
			)
		})
			this.setState({StateParks:mList});
		})
	})
}
	 render(props) { 
		  const { isLoading } = this.state;  
		const  {classes}  = this.props;
		 if(isLoading)
		 {return <Inprogress />}

  return (
	  <div>
      <Grid container className={classes.container}>
	  	{this.state.StateParks}
     </Grid>
    </div>
  );
 }
}
//export default StateParks;
//export default withStyles(useStyles)(StateParks);
StateParks.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(useStyles)(StateParks);

const styles ={
root:{
	direction:'row',
	flexGrow:1,
	},
container:{
    display: 'flex',
	 flexWrap: 'wrap',
	overflow: 'hidden',
	},
gridList: {
	padding:'0.1%',
  },

paper: {		
	width:'400px',
		height: '300px',
		listStyleType:'none',
		backgroundColor:'white',

	//border:'1px solid red',
	//[this.theme.breakpoints.up('md')]: {
    //width:'400px',
	//height: '300px',
	//},
 },
indImgs:{
	height: '100%',
	width:'100%',
	},
}

