<Grid container>
	<Grid item xs={7}>
	  	<ParkGridStateParks onClick={this.mapHandler}/>
	</Grid>
	<Grid item xs={5}>

	  	<Grid container style={styles.containerSA}>
	  	 <Grid item xs={12}>

	  	</Grid> 
	  	<Grid item xs={12} >

	  		<div style={styles.activities}>
	  <h3> Filter parks by activity</h3>
	 			{this.state.StateParks}
	   		</div>
	  	</Grid> 
	 </Grid>
	</Grid> 
</Grid> 

  );
 }
}
export default StateParks

const styles ={
	containerSA:{
		justifyContent:'center',
	},
	item:{
		textAlign:'center',
