// JavaScript Document
import React from 'react';

//Material UI
import {makeStyles} from '@material-ui/styles';
import GridList from '@material-ui/core/GridList';
import Grid from '@material-ui/core/Grid';
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';
import Inprogress from '../components/progress_indicator/inProgress'
import LinearProgress from '@material-ui/core/LinearProgress';

//console.log(window.location.pathname);
let sCode = window.location.pathname;
 let newSC = sCode.split('/stateParks/');
 newSC.shift();

const targetId = newSC;
const endpoint = `statecode=${targetId}`;

//Smart Component
class StateParks extends React.Component {
	//declaring state and new object
	state ={
		picListing: [],
		loading: false,
        progress: 0,
	}

parkClick(event) {
	window.onclick= event => {
		//console.log(event.target);

		var parkId = event.target.id;
		window.location.assign(`http://localhost:3000/park/${parkId}`);
	};
} 

//calling fetchData function
	componentDidMount(){
	this.fetchData();
}

filterByActivity(current_value, i){

			for(let i=0; i< current_value.activities.length;i++ ) {
				let activity = current_value.activities[i];
				if(activity.id ==='7CE6E935-F839-4FEC-A63E-052B1DEF39D2')
					return true;
			}
			//console.log(current_value);
		return false;
}

//fetching API
 fetchData(){
	 const classes = useStyles();
	 this.setState({ loading: true }, () =>{
	fetch(`https://developer.nps.gov/api/v1/parks?${endpoint}&api_key=YpbDDtsNwQRi13JXZXiN7DnEIusWnKQLsCZW11xq`)
		.then(results =>{

		return results.json();
	}).then(data =>{

		let filteredArray = data.data.filter(this.filterByActivity);

		let mList = filteredArray.map((use, i)=>{

			return(

				<GridList key={i} style={styles.gridList}>
				<GridListTile style={styles.paper}>
				<img key={use.images[0].id} id={use.parkCode} src={use.images[0].url} alt={use.images[0].altText} style={styles.indImgs} onClick={this.parkClick}/>
				<GridListTileBar title={use.images[0].title} subtitle={<span>{use.addresses[0].city}, {use.addresses[0].stateCode}</span>} />
				</GridListTile>	
				</GridList>
			)
		})

		this.setState({StateParks:mList}, { loading: true } );
	})
	 })
}

	 render() { 
		 const classes = useStyles();
		 const { data, loading } = this.state; 
		 console.log(loading);
  return (
	  <div style={styles.root}>

      <Grid container style={styles.container}>

	  <LinearProgress />

	  		{this.state.StateParks}

     </Grid>
    </div>
  );
 }
}
export default StateParks

const styles ={
root:{
	direction:'row',
	flexGrow:'1',
	},
container:{
    display: 'flex',
	 flexWrap: 'wrap',
	justifyContent: 'space-around',
	overflow: 'hidden',
	},
gridList: {
	paddingBottom:'0.2%',
  },
paper: { 
    textAlign: 'center',
		width:'399px',
		height: '299px',
		backgroundColor: 'rgb(15,15,15,0.4)',
		listStyleType:'none',
		backgroundColor:'white',
  },
indImgs:{
	height: '100%',
	width:'100%',
	},
	 margin: {
    //margin: theme.spacing(1)
  },
  progressLabel: {
    position: "absolute",
    width: "100%",
    height: "100%",
    zIndex: 1,
    maxHeight: "75px", // borderlinearprogress root.height
    textAlign: "center",
    display: "flex",
    alignItems: "center",
    "& span": {
      width: "100%"
    }
  },
}

